        { id: 'inventory', name: '인벤토리', icon: '🎒' },
        { id: 'skills', name: '스킬', icon: '✨' },
        { id: 'dungeon', name: '던전', icon: '🏰' },
        { id: 'shop', name: '상점', icon: '🛒' }
      ].map(tab => (
        <button
          key={tab.id}
          onClick={() => setCurrentTab(tab.id as any)}
          style={{
            background: currentTab === tab.id 
              ? 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)'
              : 'transparent',
            color: 'white',
            border: 'none',
            padding: '15px 20px',
            fontSize: '14px',
            cursor: 'pointer',
            flex: 1,
            transition: 'all 0.3s ease'
          }}
        >
          <div>{tab.icon}</div>
          <div style={{ fontSize: '12px', marginTop: '5px' }}>{tab.name}</div>
        </button>
      ))}
    </div>
  );

  return (
    <div style={{
      width: '100%',
      maxWidth: '1200px',
      margin: '0 auto',
      background: '#111827',
      borderRadius: '20px',
      overflow: 'hidden',
      boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    }}>
      {/* 게임 타이틀 */}
      <div style={{
        background: 'linear-gradient(135deg, #1e1b4b 0%, #312e81 50%, #1e1b4b 100%)',
        padding: '20px',
        textAlign: 'center',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at 50% 50%, rgba(147, 51, 234, 0.3) 0%, transparent 70%)'
        }} />
        <h1 style={{
          margin: 0,
          fontSize: '32px',
          fontWeight: 'bold',
          color: 'white',
          textShadow: '0 4px 20px rgba(147, 51, 234, 0.5)',
          position: 'relative',
          zIndex: 1
        }}>
          ⚔️ 방치형 RPG 어드벤처 ⚔️
        </h1>
        <p style={{
          margin: '10px 0 0 0',
          color: '#e5e7eb',
          fontSize: '16px',
          position: 'relative',
          zIndex: 1
        }}>
          레벨업, 전투, 탐험의 끝없는 모험이 시작됩니다!
        </p>
      </div>

      {/* 탭 메뉴 */}
      {renderTabs()}

      {/* 현재 탭 컨텐츠 */}
      {currentTab === 'lobby' && renderLobbyTab()}
      {currentTab === 'dungeon' && renderDungeonTab()}
      {currentTab === 'character' && renderCharacterTab()}
      {currentTab === 'inventory' && renderInventoryTab()}
      {currentTab === 'skills' && renderSkillsTab()}
      {currentTab === 'shop' && renderShopTab()}
    </div>
  );
};
