import React from 'react';

interface InputProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  label?: string;
  id?: string;
  type?: 'text' | 'email' | 'password' | 'url';
  disabled?: boolean;
  required?: boolean;
  className?: string;
}

export const Input: React.FC<InputProps> = ({
  value,
  onChange,
  placeholder,
  label,
  id,
  type = 'text',
  disabled = false,
  required = false,
  className = ''
}) => {
  return (
    <div className={className}>
      {label && (
        <label
          htmlFor={id}
          style={{
            display: 'block',
            fontSize: '14px',
            fontWeight: '500',
            color: 'var(--gray-700)',
            marginBottom: '6px'
          }}
        >
          {label}
          {required && <span style={{ color: 'var(--danger)' }}>*</span>}
        </label>
      )}
      <input
        id={id}
        type={type}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        placeholder={placeholder}
        disabled={disabled}
        required={required}
        style={{
          width: '100%',
          padding: '12px 16px',
          border: '1px solid var(--gray-300)',
          borderRadius: 'var(--border-radius)',
          fontSize: '16px',
          backgroundColor: disabled ? 'var(--gray-100)' : 'var(--white)',
          color: disabled ? 'var(--gray-500)' : 'var(--gray-900)',
          outline: 'none',
          transition: 'border-color 0.2s ease',
          fontFamily: 'inherit'
        }}
        onFocus={(e) => {
          e.target.style.borderColor = 'var(--brand-primary)';
        }}
        onBlur={(e) => {
          e.target.style.borderColor = 'var(--gray-300)';
        }}
      />
    </div>
  );
};
