    // ì¿¨ë‹¤ìš´ ì„¤ì •
    setCharacter(prev => ({
      ...prev,
      skills: prev.skills.map(s => 
        s.id === skill.id ? { ...s, currentCooldown: skill.cooldown } : s
      )
    }));
  };

  // ìŠ¤í‚¬ ë°°ìš°ê¸° (0ë ˆë²¨ì—ì„œ 1ë ˆë²¨ë¡œ ì—…ê·¸ë ˆì´ë“œ) í•¨ìˆ˜
  const learnSkill = (skill: Skill) => {
