  // ëª¬ìŠ¤í„° ìƒì„±
  const spawnMonster = () => {
    // ì´ë¯¸ ëª¬ìŠ¤í„°ê°€ ìˆê±°ë‚˜ ë˜ì „ì— ìˆìœ¼ë©´ ìƒˆë¡œ ìƒì„±í•˜ì§€ ì•ŠìŒ
    if (currentMonster || inDungeon) {
      return;
    }

    const gameTexts = (window as any).gameTexts || {
      monsters: { slime: 'ìŠ¬ë¼ì„', goblin: 'ê³ ë¸”ë¦°', orc: 'ì˜¤í¬', troll: 'íŠ¸ë¡¤', dragon: 'ë“œë˜ê³¤' },
      combat: { appeared: 'ì´ ë‚˜íƒ€ë‚¬ë‹¤!' }
