  // ë“œë¡­ ì•„ì´í…œ ìƒì„±
  const generateDropItems = (): Item[] => {
    const dropChance = Math.random();
    if (dropChance < 0.1) { // 10% í™•ë¥ ë¡œ ì•„ì´í…œ ë“œë¡­
      const rarities: Array<'common' | 'rare' | 'epic' | 'legendary' | 'mythic'> = ['common', 'rare', 'epic', 'legendary', 'mythic'];
      const rarity = rarities[Math.floor(Math.random() * rarities.length)];
      const itemTypes = ['weapon', 'armor', 'helmet', 'boots', 'ring', 'amulet'] as const;
      const itemType = itemTypes[Math.floor(Math.random() * itemTypes.length)];

