    if (forceSpawn && currentMonster) {
      setCurrentMonster(null);
    }
    // ë˜ì „ë³„ ê³ ìœ  ëª¬ìŠ¤í„° íƒ€ì… ì •ì˜
    const getDungeonMonsters = (dungeonId: string) => {
      switch (dungeonId) {
        case 'forest': // ì–´ë‘ ì˜ ìˆ²
          return [
            { name: 'ëŠ‘ëŒ€', baseHp: 40, baseAttack: 10, baseDefense: 3 },
            { name: 'ê³°', baseHp: 80, baseAttack: 16, baseDefense: 6 },
            { name: 'ê±°ëŒ€ ê±°ë¯¸', baseHp: 120, baseAttack: 22, baseDefense: 10 },
            { name: 'ìˆ²ì˜ ìˆ˜í˜¸ì', baseHp: 200, baseAttack: 35, baseDefense: 18 },
            { name: 'ê³ ëŒ€ ì—”íŠ¸', baseHp: 350, baseAttack: 45, baseDefense: 25 }
          ];
        case 'cave': // ê³ ë¸”ë¦° ë™êµ´
          return [
            { name: 'ê³ ë¸”ë¦° ì „ì‚¬', baseHp: 60, baseAttack: 14, baseDefense: 5 },
            { name: 'ê³ ë¸”ë¦° ê¶ìˆ˜', baseHp: 45, baseAttack: 18, baseDefense: 3 },
            { name: 'ê³ ë¸”ë¦° ë§ˆë²•ì‚¬', baseHp: 50, baseAttack: 20, baseDefense: 4 },
            { name: 'ê³ ë¸”ë¦° ìš°ë‘ë¨¸ë¦¬', baseHp: 180, baseAttack: 30, baseDefense: 15 },
            { name: 'ê³ ë¸”ë¦° ì™•', baseHp: 400, baseAttack: 50, baseDefense: 28 }
          ];
        case 'tower': // ë§ˆë²•ì‚¬ì˜ íƒ‘
          return [
            { name: 'ë§ˆë²• ê³¨ë ˜', baseHp: 100, baseAttack: 20, baseDefense: 12 },
            { name: 'ìŠ¤ì¼ˆë ˆí†¤ ë©”ì´ì§€', baseHp: 70, baseAttack: 25, baseDefense: 8 },
            { name: 'í”Œë ˆì„ ì—˜ë¦¬ë©˜íƒˆ', baseHp: 110, baseAttack: 28, baseDefense: 10 },
            { name: 'ë¦¬ì¹˜', baseHp: 250, baseAttack: 40, baseDefense: 20 },
            { name: 'ê³ ëŒ€ ë§ˆë²•ì‚¬', baseHp: 500, baseAttack: 60, baseDefense: 35 }
          ];
        default:
          return [
            { name: 'ìŠ¬ë¼ì„', baseHp: 30, baseAttack: 8, baseDefense: 2 },
            { name: 'ê³ ë¸”ë¦°', baseHp: 50, baseAttack: 12, baseDefense: 4 },
            { name: 'ì˜¤í¬', baseHp: 100, baseAttack: 18, baseDefense: 8 },
            { name: 'íŠ¸ë¡¤', baseHp: 150, baseAttack: 25, baseDefense: 12 },
