import React, { useState, useEffect, useRef } from 'react';

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface User {
  id: string;
  name: string;
  avatar: string;
  level: number;
  points: number;
  streak: number;
  badges: string[];
  joinDate: string;
}

interface Notification {
  id: string;
  title: string;
  message: string;
