      id: 1,
      title: "ThinkingDataÎûÄ?",
      subtitle: "ÌîåÎû´Ìèº ÏÜåÍ∞úÏôÄ ÌïµÏã¨ Í∏∞Îä•",
      image: "üìä",
      featured: false,
      category: "Learning"
    },
    {
      id: 2,
      title: "ÏÇ¨Ïö©Ïûê Í∞ÄÏù¥Îìú", 
      subtitle: "Í∏∞Î≥∏ ÏÇ¨Ïö©Î≤ïÎ∂ÄÌÑ∞ Í≥†Í∏â Í∏∞Îä•ÍπåÏßÄ",
      image: "üë•",
      featured: false,
      category: "Guide"
    },
    {
      id: 3,
      title: "Í∞úÎ∞úÏûê Î¨∏ÏÑú",
      subtitle: "API ÌÜµÌï© Î∞è SDK ÏÇ¨Ïö©Î≤ï",
      image: "üíª",
      featured: false,
      category: "Development"
    },
    {
      id: 4,
      title: "Í¥ÄÎ¶¨Ïûê ÎèÑÍµ¨",
      subtitle: "Í≥†Í∏â ÏÑ§Ï†ï Î∞è Í¥ÄÎ¶¨ Í∏∞Îä•",
      image: "‚öôÔ∏è",
      featured: false,
      category: "Admin"
    },
    {
      id: 5,
      title: "Í≥†Í∞ù ÏßÄÏõê",
      subtitle: "Í∏∞Ïà† ÏßÄÏõê Î∞è Î¨∏Ïùò",
      image: "üí¨",
      featured: false,
      category: "Support"
    }
  ];

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a1428 0%, #1a2332 50%, #2d3748 100%)',
      color: 'white',
      fontFamily: 'Segoe UI, system-ui, sans-serif',
      overflow: 'hidden'
    }}>
      {/* Xbox Ïä§ÌÉÄÏùº ÏÉÅÎã® Î∞î */}
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        height: '48px',
        background: 'rgba(0, 0, 0, 0.85)',
        backdropFilter: 'blur(10px)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0 20px',
        zIndex: 1000,
        borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
      }}>
        {/* ÏôºÏ™Ω: ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <div style={{
            width: '32px',
            height: '32px',
            borderRadius: '50%',
            background: 'linear-gradient(135deg, #0078d4, #106ebe)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            üìä
          </div>
          <span style={{
            color: 'white',
            fontSize: '14px',
            fontWeight: '500'
          }}>
            ThinkingData Master
          </span>
        </div>

        {/* Í∞ÄÏö¥Îç∞: ÏãúÍ∞Ñ */}
        <div style={{
          color: 'rgba(255, 255, 255, 0.8)',
          fontSize: '14px',
          fontWeight: '400'
        }}>
          {currentTime.toLocaleTimeString('ko-KR', { 
            hour: '2-digit', 
            minute: '2-digit' 
          })}
        </div>

        {/* Ïò§Î•∏Ï™Ω: ÏïÑÏù¥ÏΩòÎì§ */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <Search size={20} style={{ color: 'rgba(255, 255, 255, 0.7)', cursor: 'pointer' }} />
          <User size={20} style={{ color: 'rgba(255, 255, 255, 0.7)', cursor: 'pointer' }} />
        </div>
      </div>

      {/* Î©îÏù∏ Ïª®ÌÖåÏù¥ÎÑà */}
      <div style={{
        marginTop: '48px',
        padding: '20px',
        height: 'calc(100vh - 48px)',
        display: 'grid',
        gridTemplateColumns: isMobile ? '1fr' : '280px 1fr',
        gridTemplateRows: isMobile ? '1fr auto' : '1fr',
        gap: '20px'
      }}>

        {/* ÏôºÏ™Ω ÏÇ¨Ïù¥ÎìúÎ∞î */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          borderRadius: '12px',
          padding: '16px',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          order: isMobile ? 2 : 1
        }}>
          <div style={{ marginBottom: '24px' }}>
            <h2 style={{
              color: 'white',
              fontSize: '16px',
              fontWeight: '600',
              margin: '0 0 16px 0',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <Home size={18} />
              ÎÇ¥ Í≤åÏûÑ & Ïï±
            </h2>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
              {[
                { icon: 'üöÄ', name: 'Îπ†Î•∏ ÏãúÏûë Í∞ÄÏù¥Îìú', category: 'Quick Start', active: true, onClick: navigateToQuickStart },
                { icon: 'üìä', name: 'ThinkingData ÏÜåÍ∞ú', category: 'Learning' },
                { icon: 'üë•', name: 'ÏÇ¨Ïö©Ïûê Í∞ÄÏù¥Îìú', category: 'User Guide' },
                { icon: 'üíª', name: 'Í∞úÎ∞úÏûê Î¨∏ÏÑú', category: 'Developer' },
                { icon: '‚öôÔ∏è', name: 'Í¥ÄÎ¶¨Ïûê ÎèÑÍµ¨', category: 'Admin' }
              ].map((item, index) => (
                <div 
                  key={index}
                  onClick={item.onClick}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '12px',
                    padding: '12px',
                    borderRadius: '8px',
                    background: item.active ? 'rgba(0, 120, 212, 0.2)' : 'transparent',
                    border: item.active ? '1px solid rgba(0, 120, 212, 0.4)' : '1px solid transparent',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    if (!item.active) {
                      (e.currentTarget as HTMLElement).style.background = 'rgba(255, 255, 255, 0.05)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (!item.active) {
                      (e.currentTarget as HTMLElement).style.background = 'transparent';
                    }
                  }}
                >
                  <div style={{
                    width: '32px',
                    height: '32px',
                    borderRadius: '6px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '16px'
                  }}>
                    {item.icon}
                  </div>
                  <div style={{ flex: 1, minWidth: 0 }}>
                    <div style={{
                      color: 'white',
                      fontSize: '13px',
                      fontWeight: '500',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}>
                      {item.name}
                    </div>
                    <div style={{
                      color: 'rgba(255, 255, 255, 0.6)',
                      fontSize: '11px'
                    }}>
                      {item.category}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Ï∂îÏ≤ú Î∞è ÌÜµÍ≥Ñ */}
          <div>
            <h3 style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '14px',
              fontWeight: '600',
              margin: '0 0 12px 0',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              ÏµúÍ∑º ÌôúÎèô
            </h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
              {[
                { icon: 'üìà', text: 'Îç∞Ïù¥ÌÑ∞ Î∂ÑÏÑù ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±', time: '2ÏãúÍ∞Ñ Ï†Ñ' },
                { icon: 'üîó', text: 'API Ïó∞Îèô ÏôÑÎ£å', time: '1Ïùº Ï†Ñ' },
                { icon: 'üë•', text: 'ÌåÄÏõê 3Î™Ö Ï¥àÎåÄ', time: '3Ïùº Ï†Ñ' }
              ].map((item, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  padding: '8px',
                  borderRadius: '6px',
                  background: 'rgba(255, 255, 255, 0.02)',
                  cursor: 'pointer'
                }}>
                  <span style={{ fontSize: '14px' }}>{item.icon}</span>
                  <div style={{ flex: 1, minWidth: 0 }}>
                    <div style={{
                      color: 'rgba(255, 255, 255, 0.9)',
                      fontSize: '12px',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}>
                      {item.text}
                    </div>
                    <div style={{
                      color: 'rgba(255, 255, 255, 0.5)',
                      fontSize: '10px'
                    }}>
                      {item.time}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Î©îÏù∏ Ïª®ÌÖêÏ∏† ÏòÅÏó≠ */}
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '20px',
          order: isMobile ? 1 : 2
        }}>

          {/* Î©îÏù∏ ÌûàÏñ¥Î°ú Ïπ¥Îìú (ÌÅ∞ Ïπ¥Îìú) */}
          <div 
            onClick={navigateToQuickStart}
            style={{
              position: 'relative',
              height: isMobile ? '200px' : '300px',
              borderRadius: '16px',
              overflow: 'hidden',
              background: `
                linear-gradient(135deg, rgba(0, 120, 212, 0.8) 0%, rgba(16, 110, 190, 0.9) 100%),
                url('data:image/svg+xml,${encodeURIComponent(`
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 800">
                    <rect width="1200" height="800" fill="%23106ebe"/>
                    <circle cx="200" cy="200" r="100" fill="%23ffffff" opacity="0.1"/>
                    <circle cx="900" cy="150" r="80" fill="%23ffffff" opacity="0.08"/>
                    <circle cx="1100" cy="400" r="120" fill="%23ffffff" opacity="0.06"/>
                  </svg>
                `)}')
              `,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              cursor: 'pointer',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
              border: '2px solid rgba(255, 255, 255, 0.2)'
            }}
            onMouseEnter={(e) => {
              const element = e.currentTarget as HTMLElement;
              element.style.transform = 'scale(1.02)';
              element.style.boxShadow = '0 20px 40px rgba(0, 120, 212, 0.3)';
            }}
            onMouseLeave={(e) => {
              const element = e.currentTarget as HTMLElement;
              element.style.transform = 'scale(1)';
              element.style.boxShadow = 'none';
            }}
          >
            <div style={{
              position: 'absolute',
              inset: 0,
              background: 'linear-gradient(135deg, rgba(0, 0, 0, 0.3) 0%, rgba(0, 0, 0, 0.1) 100%)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              padding: isMobile ? '20px' : '40px'
            }}>
              <div>
                <div style={{
                  background: 'rgba(255, 255, 255, 0.2)',
                  padding: '6px 12px',
                  borderRadius: '20px',
                  fontSize: '11px',
                  fontWeight: '700',
                  color: 'white',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  marginBottom: '12px',
                  display: 'inline-block'
                }}>
                  ‚ú® Featured Quest
                </div>
                <h1 style={{
                  fontSize: isMobile ? '24px' : '36px',
                  fontWeight: '800',
                  color: 'white',
                  margin: '0 0 8px 0',
                  textShadow: '0 2px 12px rgba(0, 0, 0, 0.5)'
                }}>
                  Îπ†Î•∏ ÏãúÏûë Í∞ÄÏù¥Îìú
                </h1>
                <p style={{
                  fontSize: isMobile ? '14px' : '16px',
                  color: 'rgba(255, 255, 255, 0.9)',
                  margin: 0,
                  lineHeight: '1.5',
                  textShadow: '0 1px 8px rgba(0, 0, 0, 0.3)'
                }}>
                  4Îã®Í≥ÑÎ°ú ThinkingData ÎßàÏä§ÌÑ∞ÌïòÍ∏∞ üöÄ
                </p>
              </div>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '60px',
                height: '60px',
