        @keyframes slideIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @keyframes glow {
          0%, 100% { box-shadow: 0 0 20px rgba(0, 120, 212, 0.3); }
          50% { box-shadow: 0 0 30px rgba(0, 120, 212, 0.6); }
        }
      `}</style>
    </div>
  );
};
