import React, { useState } from 'react';
import { Building, Users, Database, BarChart, Gamepad2, Play, Trophy, Zap } from 'lucide-react';
import { StepProgress, StepButtons } from '../components/StepNavigation';
import { Step1ProjectCreation } from './steps/Step1ProjectCreation';
import { Step2AccountSetup } from './steps/Step2AccountSetup';
import { Step3DataIntegration } from './steps/Step3DataIntegration';
import { Step4AnalyticsStart } from './steps/Step4AnalyticsStart';

interface StepCompletion {
  step1: boolean;
  step2: boolean;
  step3: boolean;
  step4: boolean;
}

interface AppData {
  appId: string;
  dataUrl: string;
  selectedVersion: 'saas' | 'private' | null;
}

export const QuickStartPage: React.FC = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [stepCompletion, setStepCompletion] = useState<StepCompletion>({
    step1: false,
    step2: false,
    step3: false,
    step4: false
  });
  const [appData, setAppData] = useState<AppData>({
    appId: '',
    dataUrl: '',
    selectedVersion: null
  });

  const steps = [
    {
      id: 1,
      title: '프로젝트 생성',
      icon: <Zap size={20} />,
      completed: stepCompletion.step1,
      gradient: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
      description: '새로운 프로젝트를 생성하고 환경을 설정합니다'
    },
    {
      id: 2,
      title: '계정 및 권한 설정',
      icon: <Users size={20} />,
      completed: stepCompletion.step2,
      gradient: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
      description: '팀원 초대 및 권한 관리를 설정합니다'
    },
    {
      id: 3,
      title: '데이터 연동',
      icon: <Database size={20} />,
      completed: stepCompletion.step3,
      gradient: 'linear-gradient(135deg, #06b6d4, #0891b2)',
      description: '데이터 소스와 연결하여 수집을 시작합니다'
    },
    {
      id: 4,
      title: '분석 시작',
      icon: <Trophy size={20} />,
      completed: stepCompletion.step4,
      gradient: 'linear-gradient(135deg, #f59e0b, #d97706)',
      description: '완료! 이제 데이터 분석을 시작해보세요'
    }
  ];

  const handleStepComplete = (step: number) => {
    setStepCompletion(prev => ({
      ...prev,
      [`step${step}`]: true
    }));
  };

  const handleNext = () => {
    if (currentStep < 4 && stepCompletion[`step${currentStep}` as keyof StepCompletion]) {
      setCurrentStep(prev => prev + 1);
    }
  };

  const handlePrev = () => {
    if (currentStep > 1) {
      setCurrentStep(prev => prev - 1);
    }
  };

  const handleStepChange = (stepId: number) => {
    // Only allow navigation to completed steps or the next incomplete step
    const canNavigate = steps.find(s => s.id === stepId)?.completed || stepId === currentStep;
    if (canNavigate) {
      setCurrentStep(stepId);
    }
  };

  const canGoNext = stepCompletion[`step${currentStep}` as keyof StepCompletion];
  const canGoPrev = currentStep > 1;

  const renderCurrentStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <Step1ProjectCreation
            onComplete={() => handleStepComplete(1)}
            onVersionSelect={(version) => setAppData(prev => ({ ...prev, selectedVersion: version }))}
            selectedVersion={appData.selectedVersion}
          />
        );
      case 2:
        return (
          <Step2AccountSetup
            onComplete={() => handleStepComplete(2)}
          />
        );
      case 3:
        return (
          <Step3DataIntegration
            onComplete={() => handleStepComplete(3)}
            appData={appData}
            onAppDataChange={setAppData}
          />
        );
      case 4:
        return (
          <Step4AnalyticsStart
            onComplete={() => handleStepComplete(4)}
            appData={appData}
          />
        );
      default:
        return null;
    }
  };

  // 게이밍 테마 색상
  const theme = {
    background: 'radial-gradient(ellipse at top, #1e1b4b 0%, #0f0f23 50%, #000000 100%)',
    cardBg: 'linear-gradient(145deg, rgba(30, 30, 60, 0.9), rgba(15, 15, 35, 0.9))',
    cardBorder: 'rgba(139, 92, 246, 0.3)',
    text: '#ffffff',
    textSecondary: '#a1a1aa',
    headerBg: 'rgba(15, 15, 35, 0.8)',
    accent: '#8b5cf6',
    accentSecondary: '#3b82f6'
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: theme.background,
      padding: '24px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        {/* 게이밍 스타일 헤더 */}
        <div style={{ 
          background: theme.cardBg,
          backdropFilter: 'blur(20px)',
          borderRadius: '24px',
          padding: '48px',
          marginBottom: '32px',
          border: `1px solid ${theme.cardBorder}`,
          boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)',
          position: 'relative',
          overflow: 'hidden',
          textAlign: 'center'
        }}>
          {/* 배경 장식 */}
          <div style={{
            position: 'absolute',
            top: '-30%',
            left: '-10%',
            width: '150px',
            height: '150px',
            background: 'linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1))',
            borderRadius: '50%',
            filter: 'blur(40px)'
          }} />
          <div style={{
            position: 'absolute',
            bottom: '-30%',
            right: '-10%',
            width: '200px',
            height: '200px',
            background: 'linear-gradient(135deg, rgba(245, 158, 11, 0.1), rgba(239, 68, 68, 0.1))',
            borderRadius: '50%',
            filter: 'blur(40px)'
          }} />

          <div style={{ position: 'relative', zIndex: 1 }}>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center', 
              gap: '20px',
              marginBottom: '24px'
            }}>
              <div style={{
                width: '80px',
                height: '80px',
                background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
                borderRadius: '24px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 8px 20px rgba(139, 92, 246, 0.4)'
              }}>
                <Gamepad2 size={40} style={{ color: 'white' }} />
              </div>
              <div>
                <h1 style={{ 
                  fontSize: '32px', 
                  fontWeight: '800', 
                  background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  margin: 0,
                  lineHeight: '1.2'
                }}>
                  ThinkingData Quest
                </h1>
                <p style={{
                  fontSize: '16px',
                  color: theme.textSecondary,
                  margin: 0,
                  fontWeight: '500'
                }}>
                  데이터 마스터가 되는 여정
                </p>
              </div>
            </div>

            <h2 style={{ 
              fontSize: '28px', 
              fontWeight: '700', 
              color: theme.text,
              margin: '0 0 16px 0'
            }}>
              🚀 빠른 시작 미션
            </h2>

            <p style={{ 
              fontSize: '18px', 
              color: theme.textSecondary,
              lineHeight: '1.6',
              maxWidth: '600px',
              margin: '0 auto 24px'
            }}>
              4단계 미션을 완료하여 ThinkingData 전문가가 되어보세요!<br />
              각 단계를 클리어하고 최종 보상을 획득하세요.
            </p>

            {/* 퀵 스타트 통계 */}
            <div style={{
              display: 'flex',
              justifyContent: 'center',
              gap: '32px',
              marginTop: '24px'
            }}>
              {[
                { label: '완료된 미션', value: `${Object.values(stepCompletion).filter(Boolean).length}/4`, color: '#10b981' },
                { label: '현재 단계', value: `${currentStep}단계`, color: '#8b5cf6' },
                { label: '진행률', value: `${Math.round((Object.values(stepCompletion).filter(Boolean).length / 4) * 100)}%`, color: '#f59e0b' }
              ].map((stat, index) => (
                <div key={index} style={{ textAlign: 'center' }}>
                  <div style={{
                    fontSize: '24px',
                    fontWeight: '800',
                    color: stat.color,
                    textShadow: `0 0 10px ${stat.color}40`
                  }}>
                    {stat.value}
                  </div>
                  <div style={{
                    fontSize: '12px',
                    color: theme.textSecondary,
                    marginTop: '4px'
                  }}>
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Step Progress */}
        <StepProgress
          steps={steps}
          currentStep={currentStep}
          onStepChange={handleStepChange}
        />

        {/* Step Content */}
        <div style={{
          backgroundColor: 'var(--white)',
          borderRadius: 'var(--border-radius-lg)',
          boxShadow: 'var(--shadow)',
          padding: '32px',
          minHeight: '500px'
        }}>
          {renderCurrentStep()}
        </div>

        {/* Step Navigation Buttons */}
        <StepButtons
          currentStep={currentStep}
          totalSteps={steps.length}
          canGoNext={canGoNext}
          canGoPrev={canGoPrev}
          onNext={handleNext}
          onPrev={handlePrev}
        />
      </div>
    </div>
  );
};
