import React from 'react';
import { ChevronLeft, ChevronRight, Check } from 'lucide-react';
import { Button } from './Button';

interface Step {
  id: number;
  title: string;
  icon: React.ReactNode;
  completed: boolean;
}

interface StepNavigationProps {
  steps: Step[];
  currentStep: number;
  onStepChange: (step: number) => void;
  canGoNext: boolean;
  canGoPrev: boolean;
  onNext: () => void;
  onPrev: () => void;
}

interface StepProgressProps {
  steps: Step[];
  currentStep: number;
  onStepChange: (step: number) => void;
}

interface StepButtonsProps {
  currentStep: number;
  totalSteps: number;
  canGoNext: boolean;
  canGoPrev: boolean;
  onNext: () => void;
  onPrev: () => void;
}

export const StepProgress: React.FC<StepProgressProps> = ({
  steps,
  currentStep,
  onStepChange
}) => {
  const currentStepIndex = currentStep - 1;
  const completedSteps = steps.filter(step => step.completed).length;
  const progressPercentage = (completedSteps / steps.length) * 100;

  return (
    <div style={{ marginBottom: 'var(--space-8)' }} className="animate-fade-in">
      {/* Progress Bar */}
      <div style={{ marginBottom: 'var(--space-6)' }}>
        <div style={{
          width: '100%',
          height: '10px',
          background: 'var(--glass-bg)',
          border: '1px solid var(--glass-border)',
          borderRadius: 'var(--radius-full)',
          overflow: 'hidden',
          backdropFilter: 'var(--glass-backdrop)'
        }}>
          <div style={{
            width: `${progressPercentage}%`,
            height: '100%',
            background: 'var(--gradient-primary)',
            borderRadius: 'var(--radius-full)',
            boxShadow: '0 0 10px rgba(59, 130, 246, 0.3)',
            transition: 'var(--transition-slow)'
          }} />
        </div>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginTop: 'var(--space-2)',
          fontSize: 'var(--font-size-sm)',
          color: 'var(--gray-600)',
          fontWeight: '500'
        }}>
          <span>진행률</span>
          <span>{completedSteps}/{steps.length} 단계 완료</span>
        </div>
      </div>

      {/* Step Indicators */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: 'var(--space-8)',
        gap: 'var(--space-2)',
        overflowX: 'auto',
        padding: 'var(--space-2) 0',
        background: 'var(--glass-bg)',
        borderRadius: 'var(--radius-xl)',
        backdropFilter: 'var(--glass-backdrop)',
        border: '1px solid var(--glass-border)'
      }} className="glass-card">
        {steps.map((step, index) => (
          <React.Fragment key={step.id}>
            <div
              onClick={() => step.completed && onStepChange(step.id)}
              style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                cursor: step.completed ? 'pointer' : 'default',
                minWidth: '120px',
                padding: 'var(--space-2)',
                transition: 'var(--transition-fast)'
              }}
            >
              {/* Step Circle */}
              <div style={{
                width: '56px',
                height: '56px',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                marginBottom: 'var(--space-2)',
                background: step.completed 
                  ? 'var(--gradient-secondary)' 
                  : currentStep === step.id 
                  ? 'var(--gradient-primary)' 
                  : 'var(--gray-200)',
                color: step.completed || currentStep === step.id 
                  ? 'white' 
                  : 'var(--gray-500)',
                border: currentStep === step.id 
                  ? '3px solid var(--primary-300)' 
                  : '3px solid transparent',
                boxShadow: (step.completed || currentStep === step.id) 
                  ? 'var(--shadow-lg)' 
                  : 'var(--shadow-sm)',
                transform: currentStep === step.id ? 'scale(1.05)' : 'scale(1)',
                transition: 'var(--transition-fast)'
              }} className={currentStep === step.id ? 'animate-bounce-in' : ''}>
                {step.completed ? <Check size={20} /> : step.icon}
              </div>

              {/* Step Title */}
              <span style={{
                fontSize: 'var(--font-size-xs)',
                textAlign: 'center',
                fontWeight: currentStep === step.id ? '700' : '500',
                color: currentStep === step.id 
                  ? 'var(--primary-600)' 
                  : step.completed 
                  ? 'var(--secondary-600)' 
                  : 'var(--gray-500)',
                lineHeight: 'var(--line-height-tight)',
                textShadow: currentStep === step.id ? '0 1px 2px rgba(0,0,0,0.1)' : 'none'
              }}>
                {step.title}
              </span>
            </div>

            {/* Connector Line */}
            {index < steps.length - 1 && (
              <div style={{
                flex: 1,
                height: '4px',
                background: step.completed 
                  ? 'var(--gradient-primary)' 
                  : 'var(--gray-200)',
                borderRadius: 'var(--radius-full)',
                minWidth: '20px',
                boxShadow: step.completed ? '0 0 8px rgba(59, 130, 246, 0.3)' : 'none',
                transition: 'var(--transition-fast)'
              }} />
            )}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
};

export const StepButtons: React.FC<StepButtonsProps> = ({
  currentStep,
  totalSteps,
  canGoNext,
  canGoPrev,
  onNext,
  onPrev
}) => {
  return (
    <div style={{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      gap: 'var(--space-4)',
      padding: 'var(--space-4)',
      background: 'var(--glass-bg)',
      borderRadius: 'var(--radius-xl)',
      backdropFilter: 'var(--glass-backdrop)',
      border: '1px solid var(--glass-border)',
      marginTop: 'var(--space-8)'
    }} className="glass-card animate-slide-up">
      <Button
        variant="outline"
        onClick={onPrev}
        disabled={!canGoPrev}
        style={{ minWidth: '120px' }}
      >
        <ChevronLeft size={16} />
        이전 단계
      </Button>

      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: 'var(--space-2)',
        fontSize: 'var(--font-size-lg)',
        color: 'var(--gray-700)',
        fontWeight: '600',
        background: 'var(--gradient-text)'
      }} className="gradient-text">
        <span>{currentStep}</span>
        <span>/</span>
        <span>{totalSteps}</span>
      </div>

      {currentStep === totalSteps ? (
        <Button
          variant="primary"
          onClick={() => window.open('https://www.thinkingdata.kr/', '_blank')}
          style={{ minWidth: '120px' }}
        >
          🏠 홈으로
        </Button>
      ) : (
        <Button
          variant="primary"
          onClick={onNext}
          disabled={!canGoNext}
          style={{ minWidth: '120px' }}
        >
          다음 단계
          <ChevronRight size={16} />
        </Button>
      )}
    </div>
  );
};

// 기존 StepNavigation을 호환성을 위해 유지 (deprecated)
export const StepNavigation: React.FC<StepNavigationProps> = (props) => {
  return <StepProgress steps={props.steps} currentStep={props.currentStep} onStepChange={props.onStepChange} />;
};
